import React, { useState, useEffect } from "react";
import axios from "axios";
import { FaCheck, FaPlus } from "react-icons/fa";

const App = () => {
    const [tasks, setTasks] = useState([]);
    const [newTask, setNewTask] = useState({ title: "", description: "" });

    useEffect(() => {
        fetchTasks();
    }, []);

    const fetchTasks = async () => {
        const response = await axios.get("http://localhost:3001/tasks");
        setTasks(response.data);
    };

    const createTask = async () => {
        if (newTask.title.trim() && newTask.description.trim()) {
            await axios.post("http://localhost:3001/tasks", newTask);
            setNewTask({ title: "", description: "" });
            fetchTasks();
        }
    };

    const markAsCompleted = async (id) => {
        await axios.patch(`http://localhost:3001/tasks/${id}`);
        fetchTasks();
    };

    return (
        <div className="min-h-screen bg-gray-100 flex flex-col items-center p-4">
            <h1 className="text-4xl font-bold mb-6 text-blue-600">Todo App</h1>
            {/* New Task Section */}
            <div className="bg-white shadow-md rounded-lg p-4 mb-6 w-full max-w-md">
                <input
                    type="text"
                    placeholder="Title"
                    className="w-full mb-2 p-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
                    value={newTask.title}
                    onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
                />
                <textarea
                    placeholder="Description"
                    className="w-full mb-2 p-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
                    value={newTask.description}
                    onChange={(e) => setNewTask({ ...newTask, description: e.target.value })}
                ></textarea>
                <button
                    onClick={createTask}
                    className="bg-blue-500 text-white py-2 px-4 rounded flex items-center justify-center hover:bg-blue-600 transition"
                >
                    <FaPlus className="mr-2" /> Add Task
                </button>
            </div>
            {/* Tasks List */}
            <ul className="w-full max-w-md space-y-4">
                {tasks.map((task) => (
                    <li
                        key={task.id}
                        className="bg-white shadow-md rounded-lg p-4 flex justify-between items-start"
                    >
                        <div>
                            <h2 className="text-xl font-semibold text-gray-800">{task.title}</h2>
                            <p className="text-gray-600">{task.description}</p>
                        </div>
                        <button
                            onClick={() => markAsCompleted(task.id)}
                            className="bg-green-500 text-white p-2 rounded-full hover:bg-green-600 transition"
                        >
                            <FaCheck />
                        </button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default App;
